package Assignments;

public class Assignment_107 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		double b;
		b=a;//implicit type casting
		System.out.println("implicit widening="+b);
		double c=(double) a;//explicit widening
		System.out.println("Explicit widening="+c);
		
		

	}

}
